<script>
(function(){
    // eslint-disable-next-line require-jsdoc
    function addWishlistedClass(tile) {
        $('.product-tile', tile).find('.image-container').addClass('wishlist_checked');
        $('.wishlistTile', tile).find('span').addClass('svg-svg-96-heart-gray svg-svg-96-heart-gray-dims');
        $('.wishlistTile', tile).removeClass('select-wishlist').addClass('deselect-wishlist');
    }

    function updateWishListUI(wishlistIds) {
        wishlistIds.forEach(function (id) {
            var tile = $('div[data-tile-pid=' + id + ']');
            if (tile.length > 0) {
                addWishlistedClass(tile);
            }
            var productDetail = $('.product-detail');
            productDetail.each(function () {
                var item = $(this);
                if (item.length > 0 && item.data('pid') === id) { // eslint-disable-line
                    $('.wishlistTile', item.find('.primary-images-container')).removeClass('pdpdeselect-wishlist').removeClass('pdpselect-wishlist').addClass('pdpdeselect-wishlist');
                }
            });
        });
    }

    if (window.wishlistIds) {
        updateWishListUI(window.wishlistIds);
    }
    else {
        var wishlistURL = $('body').data('wishlisturl');
        if (wishlistURL) {
            $('body').data('wishlisturl', '');
            $.ajax({
                url: wishlistURL,
                success: function (response) {
                    if (response) {
                        window.wishlistIds = response.wishlistProducts.map(product => product.id);
                        updateWishListUI(window.wishlistIds);
                        $('body').data('wishlisturl', wishlistURL);
                    }
                },
                error: function (err) { // eslint-disable-line
                    // do nothing
                }
            });
        }
    }
    try {
        window.hbcSlider.hbcSliderInit('recommendation-products');
        window.hbcSlider.hbcSliderInit('homepage-cat-slider');
    } catch (e) {}

})();
