	setPageName	: function() {
		var site = _satellite.getVar('Site_Name');
		var nameEls = [site === 'off5th.com' ? 'off 5th' : site];
		
		var type = _satellite.getVar('Page_Type') || 'other';
		if (pageData.eventData && pageData.eventData.name && pageData.eventData.name === 'quickLook') {
			type = 'quicklook';
		}
		switch (type) {
			case 'home page':
			case 'shopping bag':
			case 'checkout confirmation':
			case 'borderfree checkout confirmation':
			case 'saks canada':
			case 'text signup':
			case 'text signup success':
				nameEls.push(type);
				if (pageData.page.gender) {
					nameEls.push(pageData.page.gender);
                }
				break;
            case 'digital stylist':
			case 'bloomreach array':
				nameEls.push(type);
				if (pageData.page.name) {
					nameEls.push(pageData.page.name);
				}
				break;
			case 'section':
			case 'product array':
			case 'brand array':
			case 'sale array':
			case 'designer':
				if (pageData.page.designer) {
					nameEls.push('brand');
					if (!pageData.page.categories || pageData.page.categories.length < 1) {
						nameEls.push('designer');
						nameEls.push(pageData.page.designer);
						if (pageData.page.designer.toLowerCase() === 'chanel') {
							nameEls.push('main');
						}
					} else {
						nameEls.push(pageData.page.designer);
                    }
				}
				if (pageData.page.categories) {
					var catList = _satellite.getVar('Product_Category_List').split(':');
					for (var i=0; i<catList.length; i++) {
						if (catList[i] && catList[i] !== 'womensclothing' && !(pageData.page.designer && pageData.page.designer.toLowerCase() === catList[i])) {
							nameEls.push(catList[i]);
						}
					}
				}
				break;
			case 'editorial':
			case 'content':
				nameEls.push(type);
				nameEls.push(pageData.page.name || 'other');
				break;
			case 'designer index':
				nameEls.push('editorial');
				nameEls.push('designer-shop');
				break;
			case 'product detail':
			case 'quicklook':
				var prod = type === 'quicklook' ? pageData.eventData.products[0] : pageData.products[0];
				nameEls.push(type);
				nameEls.push(prod.brand);
				nameEls.push(prod.name);
				break;
			case 'search array':
				nameEls.push('search');
				if (pageData.search.type) {
					nameEls.push(pageData.search.type + (pageData.search.type === 'null' ? ' results' : ''));
				} else if (pageData.page.name) {
					nameEls.push(pageData.page.name);
				}
				break;
			case 'checkout':
				nameEls.push(type);
				nameEls.push(pageData.page.checkoutStep);
				break;
			case 'email signup':
				nameEls.push('contact');
				nameEls.push('email signup ' + (document.location.pathname.indexOf('EmailSignUpAction') > -1 ? 'complete' : 'start'));
				break;
			case 'account':
			case 'error':
			case 'store locator':
				nameEls.push(type);
				nameEls.push(pageData.page.name || 'other');
				break;
            case 'recommended':
				nameEls.push(pageData.page.name);
				break;
