		for (var nameElsLen=0; nameElsLen<nameEls.length; nameElsLen++) {
			nameEls[nameElsLen] = decodeURIComponent(nameEls[nameElsLen].replace(/\%/g, 'percent').replace(/\&amp;/g, '&'));
		}
		pageData.page.adobePageName = nameEls.join(':').toLowerCase();
		pageData.page.section = (nameEls[1] === 'quick look' ? 'product detail' : nameEls[1]).toLowerCase();
		if (nameEls[1] === 'quicklook') {
			pageData.page.type = 'quick look';
